import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ChevronDownIcon, SparklesIcon, CodeBracketIcon, CpuChipIcon } from '@heroicons/react/24/outline';
import { useInView } from 'react-intersection-observer';

const Hero = () => {
  const [typedText, setTypedText] = useState('');
  const [currentRoleIndex, setCurrentRoleIndex] = useState(0);
  const { ref, inView } = useInView({ threshold: 0.3 });

  const roles = [
    { text: 'AI/ML Engineer', icon: <CpuChipIcon className="w-8 h-8" /> },
    { text: 'LLM Specialist', icon: <SparklesIcon className="w-8 h-8" /> },
    { text: 'Deep Learning Expert', icon: <CodeBracketIcon className="w-8 h-8" /> },
    { text: 'Generative AI Developer', icon: <CpuChipIcon className="w-8 h-8" /> }
  ];

  const achievements = [
    { number: "3+", label: "Years Experience", delay: 0.2 },
    { number: "60%", label: "HR Workload Reduction", delay: 0.4 },
    { number: "2x", label: "Inference Speed Boost", delay: 0.6 },
    { number: "1986", label: "LeetCode Max Rating", delay: 0.8 }
  ];

  const techIcons = [
    { name: 'PyTorch', delay: 0.1 },
    { name: 'TensorFlow', delay: 0.2 },
    { name: 'LangChain', delay: 0.3 },
    { name: 'AWS', delay: 0.4 },
    { name: 'Docker', delay: 0.5 }
  ];

  useEffect(() => {
    const currentRole = roles[currentRoleIndex].text;
    let currentIndex = 0;
    let isDeleting = false;

    const typeInterval = setInterval(() => {
      if (!isDeleting) {
        if (currentIndex <= currentRole.length) {
          setTypedText(currentRole.substring(0, currentIndex));
          currentIndex++;
        } else {
          // Wait before starting to delete
          setTimeout(() => {
            isDeleting = true;
          }, 2000);
        }
      } else {
        if (currentIndex > 0) {
          setTypedText(currentRole.substring(0, currentIndex - 1));
          currentIndex--;
        } else {
          isDeleting = false;
          setCurrentRoleIndex((prev) => (prev + 1) % roles.length);
        }
      }
    }, isDeleting ? 50 : 100);

    return () => clearInterval(typeInterval);
  }, [currentRoleIndex, roles]);

  const scrollToAbout = () => {
    const aboutSection = document.getElementById('about');
    if (aboutSection) {
      aboutSection.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.8,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: 'easeOut'
      }
    }
  };

  const socialLinks = [
    {
      name: 'LinkedIn',
      url: 'https://www.linkedin.com/in/emtiazuddinahmed/',
      icon: (
        <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
          <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
        </svg>
      )
    },
    {
      name: 'GitHub',
      url: 'https://github.com/EMTIAZ-RUET',
      icon: (
        <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
